<?class Client extends MY_Controller {	function __construct() {		parent::__construct();		enableProfiler();		/* Check if user is logged in */		requireLogin();	}	/* Make sure the logged in user is trainer and the client belongs to him */	function verifyTrainer($trainer, $client) {		if ($trainer->is_trainer == 0 or !$trainer->isTrainerOf($client)) {			$data = array(				'template' => platform() . '/my/permission_denied'			);			$this->load->view(platform() . '/page', $data);			return false;		}		return true;	}	/* Client overview */	function index($clientId) {		/* Load the required models */		$this->load->model('user');		/* Load the client and trainer */		$user = $this->user->getById(activeUser()->id);		$client = $this->user->getById($clientId);		/* Make sure the logged in user is trainer and the client belongs to him */		if (!$this->verifyTrainer($user, $client->id)) {			return;		}		$data = array(			'client' => $client,			'template' => platform() . '/trainer/client'		);		/* Show template */		$this->load->view(platform() . '/page', $data);	}	/* Client training plans */	function plans($clientId) {		/* Load the required models and helpers*/		$this->load->model('user');		$this->load->helper('form');		/* Load the client and trainer */		$user = $this->user->getById(activeUser()->id);		$client = $this->user->getById($clientId);		/* Make sure the logged in user is trainer and the client belongs to him */		if (!$this->verifyTrainer($user, $client->id)) {			return;		}		$data = array(			'client' => $client,			'plans' => $client->getTrainingPlans(),			'template' => platform() . '/trainer/client_plans'		);		/* Show template */		$this->load->view(platform() . '/page', $data);	}	/* Insert training plan to database */	function createPlan() {		/* Load the required models */		$this->load->model('user');		$this->load->model('training_plan');		/* Load the client and trainer */		$user = $this->user->getById(activeUser()->id);		$clientId = $this->input->post('clientId');		/* Make sure the logged in user is trainer and the client belongs to him */		if (!$this->verifyTrainer($user, $clientId)) {			return;		}		$this->training_plan->insert($clientId, $this->input->post());		redirect('/my/clients/' . $clientId . '/plans');	}}